import React from 'react';
import { Jumbotron, Container, Row, Col } from 'react-bootstrap';

function Hero(props){
    return(
        <Jumbotron>
            <Container>
                <Row>
                    <Col>
                        { props.title && <h1>{props.title}</h1> }
                        { props.subTitle && <h1>{props.subTitle}</h1> }
                        { props.text && <h1>{props.text}</h1> }
                    </Col>
                </Row>
            </Container>
        </Jumbotron>
    );
}

export default Hero;
